select concat('delete from TransactionHistory where transaction_id = ', t.id, '; delete from AdvertiserTransaction where id = ', t.id, ';') from AdvertiserTransaction t where t.cashback_id in (select p.id from AdvertiserTransaction t left join Payable p on t.cashback_id = p.id group by p.id having count(t.id) > 1) and t.id not in (select t.id from AdvertiserTransaction t left join Payable p on t.cashback_id = p.id group by p.id having count(t.id) > 1);
